  limit #{start,jdbcType=INTEGER}

    ,#{limit,jdbcType=INTEGER}

SELECT a.create_date, b.author, b.topic_id, b.title, a.reply_content, c.avatar from reply a,topic b,user c where a.topic_id = b.topic_id and a.reply_author_id = c.user_id and a.reply_author_name = #{replyAuthorName} ORDER BY a.create_date desc
limit #{start,jdbcType=INTEGER}
,#{limit,jdbcType=INTEGER}

SELECT a.*, b.avatar FROM reply a,user b WHERE a.is_delete = 0 AND a.topic_id = #{topicId} AND a.reply_author_id = b.user_id ORDER BY a.create_date
limit #{start,jdbcType=INTEGER}
,#{limit,jdbcType=INTEGER}

select count(reply_id)
from reply t
where t.is_delete = 0 and t.topic_id = #{topicId}

select count(reply_id)
from reply t
where t.is_delete = 0 and t.reply_author_id = #{userId}

SELECT a.reply_id, a.reply_content, a.topic_id, b.title, a.reply_author_name, a.create_date FROM reply a,topic b WHERE a.is_delete = 0 AND b.is_delete = 0 AND a.topic_id = b.topic_id
AND a.reply_author_name = #{author}
AND b.title like '%${topic}%'
AND a.create_date BETWEEN #{startDate} and #{endDate}
ORDER BY a.create_date DESC
limit #{start,jdbcType=INTEGER}
,#{limit,jdbcType=INTEGER}

SELECT COUNT(1) FROM reply a,topic b WHERE a.is_delete = 0 AND b.is_delete = 0 AND a.topic_id = b.topic_id
AND a.reply_author_name = #{author}
AND b.title like '%${topic}%'
AND a.create_date BETWEEN #{startDate} and #{endDate}
