mapper.select:
		SELECT

		FROM
			follow a,user b
		where a.uid = #{uid}
		and a.fid = b.user_id
		order by a.create_date desc

            limit #{start,jdbcType=INTEGER}

                ,#{limit,jdbcType=INTEGER}

		SELECT
        	c.*
		FROM
			follow a,user b,topic c
		where a.uid = #{uid}
		and a.fid = b.user_id
		and b.user_name = c.author
		order by c.create_date desc

            limit #{start,jdbcType=INTEGER}

                ,#{limit,jdbcType=INTEGER}

		SELECT

		FROM
			follow a,user b
		where a.fid = #{fid}
		and a.uid = b.user_id
		order by a.create_date desc

            limit #{start,jdbcType=INTEGER}

                ,#{limit,jdbcType=INTEGER}

mapper.delete:
delete from follow
where uid = #{uid}
and fid = #{fid}

mapper.select:
select count(1)
from follow
where uid = #{uid}

select count(1)
from follow
where fid = #{fid}

select count(1)
from follow
where uid = #{uid}
and fid = #{fid}
